import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { iUser } from "./users";

export const UserDetails = ({ userData }: { userData: iUser[] }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedUser, setSelectedUser] = useState<iUser | undefined>();
  const user = userData.find((a: iUser) => a.id === Number(id));

  useEffect(() => {
    setSelectedUser(user);
  }, [user]);

  useEffect(() => {
    if (!selectedUser) {
      navigate("/error404");
    }
  }, [selectedUser, navigate]);

  if (!selectedUser) {
    return null;
  }

  return (
    <div>
      <h1>Bonjour {`${selectedUser.userInfos.firstName}`}</h1>
