import "./_home.scss";
import { SideNav } from "./SideNav";
import { useEffect, useState } from "react";
import { fetchMockData } from "./fetchMockData";
import { iUser } from "./users";
import { Link } from "react-router-dom";

export const Home = () => {
  const [userData, setUserData] = useState<iUser[]>([]);

  useEffect(() => {
    // Utilisez fetchData à l'intérieur d'un effet de chargement initial
    const fetchDataAndDoSomething = async () => {
      try {
        const fetchedUserData: iUser[] = await fetchMockData();
        // Utilisez les données récupérées comme nécessaire
        setUserData(fetchedUserData);
        console.log("Données dans le composant:", fetchedUserData);
      } catch (error) {
        // Gérez les erreurs ici
        console.error("Erreur dans le composant:", (error as Error).message);
      }
    };

    fetchDataAndDoSomething();
  }, []);

  return (
    <main>
      <SideNav />
      <section>
        <h1 className="title">Login</h1>
        put pic
        <div className="login">
          {userData.map((user) => (
            <Link key={user.id} to={`/user/${user.id}`}>
              <p>{`user ${user.userInfos.firstName}`}</p>
            </Link>
          ))}
        </div>
      </section>
    </main>
  );
};
