import { iUser } from "./users";

export async function fetchData(): Promise<iUser[]> {
    try {
        const response = await fetch('./mockDataUsers.json');

        if (!response.ok) {
            throw new Error(`Erreur lors de la requête : ${response.status}`);
        }

        const data: iUser[] = await response.json();
        console.log('Données récupérées avec succès:', data);
        return data;
    } catch (error: unknown) {
        console.error('Erreur lors de la récupération des données:', (error as Error).message);
        throw error;
    }
}

// Exemple d'utilisation de la fonction fetch
async function exampleUsage() {
    try {
        await fetchData();
        // Utilisez les données récupérées comme nécessaire
    } catch (error) {
        // Gérez les erreurs ici
    }
}

// Appelez la fonction d'exemple
exampleUsage();
